mapper.insert:
  		/*판매적 학력 입력*/  		
  		INSERT into tb_u_expert_education
		(	expert_final_education_code 
			,user_id_code
			,expert_school_name
			,expert_major
			,expert_final_state 
			,expert_academic_document
			,expert_reg_date
			,expert_update_date
			)values(
			'자동코드생성'
			,#{userIdCode}
			,#{expertSchoolName}
			,#{expertMajor}
			,#{expertFinalState}
			,#{expertAcademicDocument}
			,CURDATE()
			,CURDATE()
		);

  		/*판매적 근무사항 입력*/  		
  		INSERT into tb_u_expert_career
		(	expert_career_details_code 
			,user_id_code
			,expert_company_name
			,expert_work_department
			,expert_position 
			,expert_work_place
			,expert_work_date
			,expert_etc
			,expert_stay_use
			,expert_career_proof_matters_attachments
			)values(
			'자동코드생성'
			,#{userIdCode}
			,#{expertCompanyName}
			,#{expertWorkDepartment}
			,#{expertPosition}
			,#{expertWorkPlace}
			,#{expertWorkDate}
			,#{expertEtc}
			,#{expertStayUse}
			,#{expertCareerProofMattersAttachments}
		);

  		/*판매적 비지니스 입력*/  		
  		INSERT into tb_u_expert_business_filed
		(	expert_business_code 
			,user_id_code
			,expert_business_field
			,expert_detailed_business
			,expert_retention_technology 
			,expert_introduction
			,expert_reg_date
			,expert_update_date
			)values(
			'자동코드생성'
			,#{userIdCode}
			,#{expertBusinessField}
			,#{expertDetailedBusiness}
			,#{expertRetentionTechnology}
			,#{expertIntroduction}
			,CURDATE()
			,CURDATE()
		);
